import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


//class based components in React
//state in class based components
//function based components do not have their state while class based components have their state

class MySwitch extends React.Component{

  constructor(props){
    super(props);  
    this.a= 3;
    this.b=4;    
    this.addHandler = this.myHandler.bind(this);
    this.subHandler1 = this.myHandler.bind(this);
    this.mulHandler2 = this.myHandler.bind(this);
    this.divHandler3 = this.myHandler.bind(this);
  }

  addHandler(){
    this.setState(i=>(
      {result:i.a+i.b}
    ));
  }
  subHandler(){
    this.setState(i=>(
      {result:i.a-i.b}
    ));
  }
  mulHandler(){
    this.setState(i=>(
      {result:i.a*i.b}
    ));
  }
  divHandler(){
    this.setState(i=>(
      {result:i.a/i.b}
    ));
  }



  render(){
    return (
      <div>
        <h1>{this.state.result}</h1>
     <button onClick={this.addHandler}>add</button>
      <button onClick={this.subHandler}>sub</button>
      <button onClick={this.mulHandler}>mul</button>
      <button onClick={this.divHandler}>div</button>
      </div>
    );
  }
}

ReactDOM.render(  
  <MySwitch/> ,
document.getElementById('root')
);
