import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


//class based components in React
//state in class based components
//function based components do not have their state while class based components have their state

class MySwitch extends React.Component{

  constructor(props){
    super(props);  
    this.a= 20;
    this.b=20    
    this.myHandler = this.myHandler.bind(this);
    this.myHandler1 = this.myHandler.bind(this);
    this.myHandler2 = this.myHandler.bind(this);
    this.myHandler3 = this.myHandler.bind(this);
  }

  myHandler(){
    let results=parseInt(this.a)+parseInt(this.b);
    alert(results);
  }
  myHandler1(){
    let results1=parseInt(this.a)-parseInt(this.b);
    alert(results1);
  }
  myHandler2(){
    let results2=parseInt(this.a)*parseInt(this.b);
    alert(results2);
  }
  myHandler3(){
    let results3=parseInt(this.a)/parseInt(this.b);
    alert(results3);
  }



  render(){
    return (
      <div>
     <h2> <button onClick={this.myHandler}>add</button>{this.myHandler}</h2>
     <h2> <button onClick={this.myHandler1}>sub</button>{this.myHandler1}</h2>
     <h2> <button onClick={this.myHandler2}>mul</button>{this.myHandler2}</h2>
     <h2> <button onClick={this.myHandler3}>div</button>{this.myHandler3}</h2>
      </div>
    );
  }
}

ReactDOM.render(  
  <MySwitch/> ,
document.getElementById('root')
);
