import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Calculator extends Component {
  constructor(props) {
    super(props)
    this.state = {
      currNum: '',
      total: 0,
      memory: ''
    }
    this.operation = ''
  }

  render() {
    return (
      <main class="composi-calculator">
        <ContentEditable number={this.state.total} />
        <div class='calculator-body'>
          <MainPanel />
          <ButtonSetEquations />
        </div>
      </main>
    )
  }
  handleEvent(e) {
    if (e.target.nodeName === 'BUTTON') {
      const name = e.target.className
      if (name != 'equals' 
        && name !== 'delete'
        && name !== 'divide'
        && name !== 'multiply'
        && name !== 'add'
        && name !== 'subtract'
      ) {
        this.useNumber(e)
      }
      if (name === 'delete') {
        this.delete()
      }
      // Math operations:
      if (name === 'add') {
        this.setState({memory: this.state.total})
        this.setState({total: '', oldNum: '', currNum: ''})
        this.operation = '+'
      }
      if (name === 'subtract') {
        this.setState({memory: this.state.total})
        this.setState({total: '', oldNum: '', currNum: ''})
        this.operation = '-'
      }
      if (name === 'divide') {
        this.setState({memory: this.state.total})
        this.setState({total: '', oldNum: '', currNum: ''})
        this.operation = '/'
      }
      if (name === 'multiply') {
        this.setState({memory: this.state.total})
        this.setState({total: '', oldNum: '', currNum: ''})
        this.operation = "*"
      }
      // Get total from math operation:s
      if (name === 'equals') {

        const operation = this.operation
        let memory = parseFloat(this.state.memory)
        let total = parseFloat(this.state.total)

        // In case of some weird combination of key presses:
        if (!operation) this.delete()
        if (isNaN(memory)) memory = 0
        if (isNaN(total)) total = 0
        if (operation === '+') this.setState({total: memory + total})
        if (operation === '*') this.setState({total: memory * total})
        if (operation === '/') this.setState({total: memory / total})
        if (operation === '-') this.setState({total: memory - total})
      }
    }
  }
  componentWasCreated() {
    this.element.addEventListener('click', this)
  }
  useNumber(e) {
    let name = e.target.className;
    if (name === 'decimal') name = '.'
    if (this.state.total === '0') this.state.total = ''
    this.setState({currNum: name})
    if (this.state.total != 0) {
      this.setState({total: this.state.total  + this.state.currNum})
    } else {
      this.setState({total: this.state.currNum})
    }
  }

  delete() {
    this.setState({
      currNum: '',
      total: 0,
      memory: ''
    })
  }

  delete() {
    this.setState({
      currNum: '',
      total: 0,
      memory: ''
    })
  }
  add(amount) {
    let state = this.state
    state += amount
    this.setState(state)
  }
  subtract(amount) {
    let state = this.state
    state -= amount
    this.setState(state)
  }
  multiply(amount) {
    let state = this.state
    state *= amount
    this.setState(state)
  }
  divide(amount) {
    let state = this.state
    state /= amount
    this.setState(state)
  }
}
ReactDOM.render(  
  <Calculator/> ,
document.getElementById('root')
);
