import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


//class based components in React
//state in class based components
//function based components do not have their state while class based components have their state

class MySwitch extends React.Component{

  constructor(props){
    super(props);  
    this.num1= 3;
    this.num2=4;    
    this.myHandler = this.myHandler.bind(this);
    this.myHandler1 = this.myHandler.bind(this);
    this.myHandler2 = this.myHandler.bind(this);
    this.myHandler3 = this.myHandler.bind(this);
  }

  myHandler(){
    let results=parseInt(this.num1)+parseInt(this.num2);
    alert(results);
  }
  myHandler1(){
    let res1=parseInt(this.num1)-parseInt(this.num2);
    alert(res1);
  }
  myHandler2(){
    let res2=parseInt(this.num1)*parseInt(this.num2);
    alert(res2);
  }
  myHandler3(){
    let res3 = parseInt(this.num1) / parseInt(this.num2);
    alert(res3);
  }



  render(){
    return (
      <div>
        <h1>Num1={this.num1} and Num2={this.num2}</h1>
     <h2> <button onClick={this.myHandler}>add</button></h2>
     <h2> <button onClick={this.myHandler1}>sub</button></h2>
     <h2> <button onClick={this.myHandler2}>mul</button></h2>
     <h2> <button onClick={this.myHandler3}>div</button></h2>
      </div>
    );
  }
}

ReactDOM.render(  
  <MySwitch/> ,
document.getElementById('root')
);
